body {
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
  max-width: 100%;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background-color: #1c1f23;
  color: #eaedef;
  text-align: center;
}

h1 {
  font-size: 2.8rem;
  font-weight: 300;
  text-shadow: 1.5px 1.5px 3px rgba(0, 0, 0, 0.4);
}

h2 {
  font-size: 2.3rem;
  font-weight: 300;
  text-shadow: 1.5px 1.5px 3px rgba(0, 0, 0, 0.4);
}

h3 {
  font-size: 1.9rem;
  font-weight: 300;
  text-shadow: 1.5px 1.5px 4px rgba(0, 0, 0, 0.5);
}

button {
  width: 80px;
  height: 40px;
  background-color: #1c1f23;
  color: #61dafb;
  outline: 0;
  border: 1px solid #a1eaff;
  border-radius: 3px;
  box-shadow: 1px 2px 3px rgba(0, 0, 0, 0.3);
}

button:hover {
  background-color: #36394a;
  color: #61dafb;
  outline: 0;
  border: 1px solid #a1eaff;
  border-radius: 3px;
  box-shadow: 2px 3px 4px rgba(0, 0, 0, 0.9);
  transition: background-color 0.1s, box-shadow 0.2s;
}

a {
  text-decoration: none;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
  color: #ffca90;
}

code {
  font-family: source-code-pro, Menlo, Monaco, Consolas, "Courier New", monospace;
}

nav {
  background-color: #1c1f23;
  font-size: 1.1rem;
  font-weight: 350;
  overflow: hidden;
  text-align: center;
  width: 100%;
  padding: 1.8vh 0vw 1.8vh 0vw;
  box-shadow: 0px 2px 1px rgba(0, 0, 0, 0.1);
}

nav ul {
  justify-content: center;
  margin: auto;
  list-style: none;
  margin: 0vh 5vw 0vh 5vw;
}

nav li {
  float: left;
  display: block-inline;
  padding: 8px 1.3vw 8px 1.3vw;
  border-bottom: 2px solid rgba(100, 100, 100, 0);
}

nav li:hover {
  border-bottom: 2px solid #646464;
  transition: border-bottom 0.3s;
}

.site-title {
  margin-right: 5vw;
}

.app-link {
  color: #61dafb;
  text-shadow: 0px 1px 0px black;
}

.app-link:hover {
  color: #509adf;
}

.nav-right-link {
  float: right;
  text-shadow: 0px 1px 0px black;
}

.nav-right-link a {
  color: #ffca90;
}

.nav-right-link a:hover {
  color: #df9a50;
}

.app {
  text-align: center;
  display: flex;
}

@media (prefers-reduced-motion: no-preference) {
  .app-logo {
    animation: app-logo-spin infinite 20s linear;
  }
}
.app-header {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: calc(10px + 2vmin);
  color: white;
}

@keyframes app-logo-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
.wrapper {
  text-align: center;
  background-color: #282a2c;
}

.content {
  padding: 2.5vh 15vw 5vh 15vw;
  min-height: 40vh;
}

.footer {
  background-color: #1c1f23;
  color: #5c5f73;
  font-weight: 400;
  text-shadow: 0px -1px 0px #000;
}

@keyframes modal {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  1% {
    display: flex;
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}
.modal {
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.7);
  animation-duration: 0.6s;
  animation-name: modal;
}

.modal-content--image {
  margin: 5.8rem auto auto;
  display: flex;
  max-width: 100%;
  height: calc(100% - 11.6rem);
}

contact {
  align-content: center;
}

contactform fieldset {
  margin: auto;
  padding-bottom: 40px;
  margin-bottom: 20px;
  background-color: #282a2e;
  max-width: 40vw;
  text-shadow: 0px -1px 0px #000;
}

contactform p {
  text-shadow: 0px -1px 0px #000;
}

